Moves:
; Characteristics of each move.

move: macro
	db \1			; animation (interchangeable with move id)
	shift
	db \1			; effect
	db \2			; power
	db \3			; type
	db \4 percent	; accuracy
	db \5			; PP
	db (\6 << 4)|(\7 << 3)|(\8 << 2)|\9	; category, priority & additionnal flags
										; bits 7 to 4 encode the move's priority (normal priority=1000, +7=1111, -8=0000)
										; bit 3 encodes whether this is a sound-based move (1=sound-based 0=not sound-based)
										; bit 2 encodes whether this is a contact move (1=contact 0=no contact)
										; bits 0 and 1 encode the category (00=physical 01=special 10=status 11=confusion self-damage)
										; so for example (10000100) means normal priority, physical move that makes contact and isn't sound-based
										; bit-by-bit breakdown:
										; first 4 bits ([1000]0100): normal priority
										; bit 3 (1000[0]100): is not sound-based
										; bit 2 (10000[1]00): makes contact
										; last 2 bits (100001[00]): physical move

endm

	move POUND,        NO_ADDITIONAL_EFFECT,                        40, NORMAL,   100, 35, PRIORITY_NORMAL, NOT_SOUND_BASED, CONTACT,    CATEGORY_PHYSICAL
